import React from 'react';
import { useDropzone } from 'react-dropzone';

const ProfileImageModal = ({ onClose, onDelete, onUpload }) => {
  const { getRootProps, getInputProps, acceptedFiles } = useDropzone({
    accept: 'image/*',
    maxFiles: 1,
  });

  const handleUpload = () => {
    if (acceptedFiles.length > 0) {
      onUpload(acceptedFiles[0]);
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-gray-800 text-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-center text-xl font-bold mb-2">
          Upload Profile Image
        </h2>
        <p className="text-center text-sm mb-4">
          Upload a profile image to personalize your account.
        </p>

        {/* Dropzone Area */}
        <div
          {...getRootProps()}
          className="flex items-center justify-center w-full h-32 border-2 border-dashed border-gray-500 rounded-lg cursor-pointer bg-gray-700 mb-4"
        >
          <input {...getInputProps()} />
          <p className="text-center text-sm text-gray-300">
            Drag & drop an image here, or click to select a file
          </p>
        </div>

        {/* Button Section */}
        <div className="flex justify-between mt-4">
          <button
            onClick={onDelete}
            className="bg-gray-500 hover:bg-gray-400 text-white font-bold py-2 px-4 rounded transition"
          >
            Delete Picture
          </button>
          <button
            onClick={handleUpload}
            className="bg-orange-500 hover:bg-orange-400 text-white font-bold py-2 px-4 rounded transition"
          >
            Upload Image
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProfileImageModal;
