import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/profile.css';

const Profile = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    profileImage: '/images/avatar.png',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [isUpdatingPassword, setIsUpdatingPassword] = useState(false);
  const [passwordData, setPasswordData] = useState({
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve JWT token
        const response = await axios.get('http://localhost:5000/user/profile', {
          headers: {
            Authorization: token ? `Bearer ${token}` : '',
          },
        });
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const handleEditClick = () => setIsEditing(true);
  const handleEditPasswordClick = () => setIsUpdatingPassword(true);

  const handleUpdatePersonalInfo = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        'http://localhost:5000/user/profile/update',
        { username: userData.username, email: userData.email },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Profile updated successfully!');
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile info:', error);
      alert('Failed to update profile info.');
    }
  };

  const handleUpdateSecurityInfo = async (e) => {
    e.preventDefault();
    if (passwordData.password !== passwordData.confirmPassword) {
      return alert('Passwords do not match!');
    }
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        'http://localhost:5000/user/profile/security',
        { password: passwordData.password },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Password updated successfully!');
      setIsUpdatingPassword(false);
      setPasswordData({ password: '', confirmPassword: '' });
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Failed to update password.');
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <button onClick={() => navigate(-1)} className="back-button">
          ←
        </button>
        <h2>User Profile</h2>
      </div>
      <div className="profile-card cards">
        <img
          src={userData.profileImage}
          alt="Profile"
          className="profile-avatar"
        />
        <div>
          <h3>{userData.username}</h3>
          <p>{userData.email}</p>
        </div>
        <button type="button" className="edit-button">
          Change Picture ✎
        </button>
      </div>
