import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/profile.css';

const Profile = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    profileImage: '/images/avatar.png',
  });
  const [passwordData, setPasswordData] = useState({
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/user/profile', {
          headers: { Authorization: token ? `Bearer ${token}` : '' },
        });
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const handleUpdatePersonalInfo = async (e) => {
    e.preventDefault();

    // Check if the username or email has changed
    if (userData.username === '' || userData.email === '') {
      return alert('Username and Email cannot be empty.');
    }

    try {
      const token = localStorage.getItem('token');

      // Check for unique email and username
      const uniqueCheckResponse = await axios.post(
        'http://localhost:5000/user/validate',
        { username: userData.username, email: userData.email },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (!uniqueCheckResponse.data.isUnique) {
        return alert('Username or email is already in use.');
      }

      // Proceed to update user info
      await axios.put(
        'http://localhost:5000/user/profile/update',
        { username: userData.username, email: userData.email },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Profile updated successfully!');
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile info:', error);
      alert('Failed to update profile info.');
    }
  };

  const handleUpdateSecurityInfo = async (e) => {
    e.preventDefault();

    // Password validation
    if (passwordData.password.length < 6) {
      return alert('Password should be at least 6 characters long.');
    }
    if (passwordData.password !== passwordData.confirmPassword) {
      return alert('Passwords do not match!');
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        'http://localhost:5000/user/profile/security',
        { password: passwordData.password },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Password updated successfully!');
      setPasswordData({ password: '', confirmPassword: '' });
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Failed to update password.');
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <button onClick={() => navigate(-1)} className="back-button">
          ←
        </button>
        <h2>User Profile</h2>
      </div>
      <div className="profile-card cards">
        <img
          src={userData.profileImage}
          alt="Profile"
          className="profile-avatar"
        />
        <div>
          <h3>{userData.username}</h3>
          <p>{userData.email}</p>
        </div>
        <button type="button" className="edit-button">
          Change Picture ✎
        </button>
      </div>
      <div className="profile-info-section">
        <div className="profile-info cards">
          <form onSubmit={handleUpdatePersonalInfo}>
            <h4>Personal Information</h4>
            <label>
              <h5>Username:</h5>
              <input
                type="text"
                value={userData.username}
                onChange={(e) =>
                  setUserData({ ...userData, username: e.target.value })
                }
              />
            </label>
            <label>
              <h5>Email:</h5>
              <input
                type="email"
                value={userData.email}
                onChange={(e) =>
                  setUserData({ ...userData, email: e.target.value })
                }
              />
            </label>
            <button type="submit" className="update-button">
              Update ✎
            </button>
          </form>
        </div>
        <div className="security-info cards">
          <form onSubmit={handleUpdateSecurityInfo}>
            <h4>Security</h4>
            <label>
              <h5>Password:</h5>
              <input
                type="password"
                placeholder="New password"
                value={passwordData.password}
                onChange={(e) =>
                  setPasswordData({ ...passwordData, password: e.target.value })
                }
              />
            </label>
            <label>
              <h5>Confirm Password:</h5>
              <input
                type="password"
                placeholder="Confirm password"
                value={passwordData.confirmPassword}
                onChange={(e) =>
                  setPasswordData({
                    ...passwordData,
                    confirmPassword: e.target.value,
                  })
                }
              />
            </label>
            <button type="submit" className="update-button">
              Update ✎
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Profile;
