// ProfileImageModal.js
import React from 'react';
import { useDropzone } from 'react-dropzone';
import '../styles/ProfileImageModal.css';

const ProfileImageModal = ({ onClose, onUpload, onDelete }) => {
  const { getRootProps, getInputProps } = useDropzone({
    accept: 'image/*',
    onDrop: (acceptedFiles) => onUpload(acceptedFiles[0]),
  });

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Change Profile Picture</h3>
        <div {...getRootProps({ className: 'dropzone' })}>
          <input {...getInputProps()} />
          <p>Drag 'n' drop a new image here, or click to select one</p>
          <button className="remove-button" onClick={onDelete}>
            Remove Profile Picture
          </button>
          <button className="close-button" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProfileImageModal;
