import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/profile.css';

const Profile = () => {
	const navigate = useNavigate();
	const [userData, setUserData] = useState(null);
	const [isEditingProfile, setIsEditingProfile] = useState(false);
	const [isEditingPersonalInfo, setIsEditingPersonalInfo] = useState(false);
	const [isEditingSecurity, setIsEditingSecurity] = useState(false);
	const [formData, setFormData] = useState({
		username: '',
		email: '',
		password: '',
		confirmPassword: '',
		profileImage: null,
	});
	const [error, setError] = useState('');

	useEffect(() => {
		// Fetch user data on component mount
		const fetchUserData = async () => {
			try {
				const response = await fetch('/api/user'); // Adjust URL to your API
				const data = await response.json();
				setUserData(data);
				setFormData({
					username: data.username,
					email: data.email,
					password: '',
					confirmPassword: '',
					profileImage: data.profileImage || '/images/default-avatar.png',
				});
			} catch (error) {
				console.error('Failed to fetch user data:', error);
			}
		};
		fetchUserData();
	}, []);

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};

	const handleImageChange = (e) => {
		setFormData((prevData) => ({
			...prevData,
			profileImage: e.target.files[0],
		}));
	};

	const handleSave = async (section) => {
		try {
			let updateData = {};
			if (section === 'profileImage') {
				const formData = new FormData();
				formData.append('profileImage', formData.profileImage);
				await fetch('/api/user/profile-image', {
					method: 'PUT',
					body: formData,
				});
			} else if (section === 'personalInfo') {
				updateData = {
					username: formData.username,
					email: formData.email,
				};
			} else if (section === 'security') {
				if (formData.password !== formData.confirmPassword) {
					setError('Passwords do not match');
					return;
				}
				updateData = { password: formData.password };
			}

			const response = await fetch(`/api/user/update`, {
				method: 'PUT',
				headers: { 'Content-Type': 'application/json' },
				body: JSON.stringify(updateData),
			});

			if (!response.ok) {
				const errorData = await response.json();
				setError(errorData.message);
			} else {
				setIsEditingProfile(false);
				setIsEditingPersonalInfo(false);
				setIsEditingSecurity(false);
				setError('');
			}
		} catch (err) {
			console.error('Error updating data:', err);
		}
	};

	return (
		<div className="profile-container">
			<div className="profile-header">
				<button onClick={() => navigate(-1)} className="back-button">
					←
				</button>
				<h2>User Profile</h2>
			</div>
			<div className="profile-card cards">
				<input
					type="file"
					accept="image/*"
					onChange={handleImageChange}
					style={{ display: isEditingProfile ? 'block' : 'none' }}
				/>
				<img
					src={userData?.profileImage || formData.profileImage}
					alt="Profile"
					className="profile-avatar"
				/>
				<div>
					<h3>{formData.username}</h3>
					<p>{formData.email}</p>
				</div>
				<button
					onClick={() => setIsEditingProfile(!isEditingProfile)}
					className="edit-button"
				>
					{isEditingProfile ? 'Save' : 'Edit'} ✎
				</button>
			</div>
			<div className="profile-info-section">
				<div className="profile-info cards">
					<h4>Personal Information</h4>
					{isEditingPersonalInfo ? (
						<>
							<label>Username:</label>
							<input
								type="text"
								name="username"
								value={formData.username}
								onChange={handleInputChange}
							/>
							<label>Email:</label>
							<input
								type="email"
								name="email"
								value={formData.email}
								onChange={handleInputChange}
							/>
							<button
								onClick={() => handleSave('personalInfo')}
								className="save-button"
							>
								Save
							</button>
						</>
					) : (
						<>
							<h5>Username:</h5>
							<p>{formData.username}</p>
							<h5>Email:</h5>
							<p>{formData.email}</p>
							<button
								onClick={() => setIsEditingPersonalInfo(true)}
								className="edit-button"
							>
								Edit ✎
							</button>
						</>
					)}
				</div>
				<div className="security-info cards">
					<h4>Security</h4>
					{isEditingSecurity ? (
						<>
							<label>Password:</label>
							<input
								type="password"
								name="password"
								value={formData.password}
								onChange={handleInputChange}
							/>
							<label>Confirm Password:</label>
							<input
								type="password"
								name="confirmPassword"
								value={formData.confirmPassword}
								onChange={handleInputChange}
							/>
							<button
								onClick={() => handleSave('security')}
								className="save-button"
							>
								Save
							</button>
						</>
					) : (
						<>
							<h5>Password:</h5>
							<p>••••••••</p>
							<button
								onClick={() => setIsEditingSecurity(true)}
								className="edit-button"
							>
								Edit ✎
							</button>
						</>
					)}
				</div>
			</div>
			{error && <p className="error-message">{error}</p>}
		</div>
	);
};

export default Profile;
